import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';

function Input(){
  return (
    <div>
      <lable>First Name</lable>
    <input type="text" placeholder="name"/>
    <br/>
    <input type="text" placeholder="address"/>
    <br/>
    <br/>
    </div>
    ); 
}
function Button(){
  return(
    <div>
      <button type="submit">Submit</button>
    </div>
  )

}
function Header(){
  return(
  <div class="first">
    <h1>Sample Website</h1>
    <p>practice one</p>
  </div>
  );

}
function Footer(){
  return(
    <div class="head">
    <h1>Sample Website</h1>
    <p>practice one</p>
  </div>
  );

}
function Navibar()
{
  return(
  <div class="navbar">
  <a href="#">Home</a>
  <a href="#">Link1</a>
  <a href="#">Link2</a>
  <a href="#" class="right">Log out</a>
</div>
  );
}
class App extends Component {
  state = {
    persons: [
      {name: 'Lavanya', mobile:"123"},
      {name: 'Ganesh', mobile:"8093"},
    ],
    showPersons: false

  }
  switchNameHandler = (newName) => {
    console.log("Onclick events");
    alert("This is click event")
    this.setState({
      persons:[
        {name:newName,mobile:'56'},
        {name:'Shailajs',mobile:'445'}

      ]
    })
  }
  tagglePerson = () =>{
    const doesshow= this.state.showPersons
    {this.setState({showPersons:!doesshow})}
  }
  nameChangeHandler = (event) => {
    this.setState({
      persons:[
        {name:'lavanya',mobile:'56'},
        {name:event.target.value,mobile:'445'}

      ]
    })
  }
  render(){

    const style = {
      backgroundColor:'white',
      font:'inherit',
      border:'1px solid blue',
      padding:'8px',
      cursor: 'pointer'
    };
    return (
      <div>
        {/* <Input />
        <Button /> */}
        <Header />
        <Navibar />
        <button style={style} onClick ={()=> this.tagglePerson("test")}>Switch Name</button>
        { this.state.showPersons == true ?
          <div>
            <Person name={this.state.persons[0].name} mobile={this.state.persons[0].mobile}/>
            <Person name={this.state.persons[1].name} mobile={this.state.persons[1].mobile}
            click ={this.switchNameHandler.bind(this,'sunitha')} changed = {this.nameChangeHandler}>Second Person</Person>
          </div> :null
        }


      </div>
    );
  }

}
export default App;



