import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';
import person from './Person/Person';
import Sidebar from './components/Sidebar';
import Rightsidebar from './components/Rightsidebar';
import Content from './components/Content';
function Input(){
  return (
    <div>
      <lable>First Name</lable>
    <input type="text" placeholder="name"/>
    <br/>
    <input type="text" placeholder="address"/>
    <br/>
    <br/>
    </div>
    ); 
}
function Button(){
  return(
    <div>
      <button type="submit">Submit</button>
    </div>
  )

}
function Header(){
  return(
  <div class="first">
    <h1>Sample Website</h1>
    <p>practice one</p>
  </div>
  );

}
function Footer(){
  return(
    <div class="head">
    <h1>Sample Website</h1>
    <p>practice one</p>
  </div>
  );

}
function Navibar()
{
  return(
  <div class="navbar">
  <a href="#">Home</a>
  <a href="#">Link1</a>
  <a href="#">Link2</a>
  <a href="#" class="right">Log out</a>
</div>
  );
}
class App extends Component {

  state = {
     data : "Hello World",
    persons: [
      {id: 'sdfsd3', name: 'Lavanya', mobile:"123"},
      {id: 'sdfsdfsg3', name: 'Ganesh', mobile:"8093"},
      {id: '3', name: 'Venkatesh', mobile:"6676"},

    ],
    navitabs: [
          {id: "1",image:"/home/kalgudi/Downloads/home.svg",name:"Home"},
          {id: "2", image:"E",name:"Explore"},
          {id: "3", image:"N",name:"Notifications"},
          {id: "4", image:"B",name:"Bookmarks"},
          {id: "5", image:"L",name:"Lists"},
           {id: "6", image:"P",name:"Porifle"},
        ],

    likecontent:[
       {id: "1",image:"/home/kalgudi/Downloads/home.svg",name:"CGTN",tweetid:"@ONEchampionship"},
       {id: "1",image:"/home/kalgudi/Downloads/home.svg",name:"CGTN",tweetid:"@ONEchampionship"},
       {id: "1",image:"/home/kalgudi/Downloads/home.svg",name:"CGTN",tweetid:"@ONEchampionship"},
    ],
    tweets:[
      {id: "1",content:"Development and the environment are not mutually exclusive.India will progress economically and take the lead in protecting the environment. #InternationalTigerDay",
      name:"Narendra Modi",tweetid:"@narendramodi"},
      {id: "1",content:"Releasing the results of All India Tiger Estimation. #InternationalTigerDay",
      name:"Narendra Modi",tweetid:"@narendramodi"},
      {id: "1",content:"Better bonding through reading!Impressed by the diverse range of books being talked about by people on the NaMo App. On popular request, this will be a lasting feature on the App. I need your help to find a name for this section. ",
      name:"Narendra Modi",tweetid:"@narendramodi"},   ],
    showPersons: false

  }


  // switchNameHandler = (newName) => {
  //   console.log("Onclick events");
  //   alert("This is click event")
  //   this.setState({
  //     persons:[
  //       {name:newName,mobile:'56'},
  //       {name:'Shailajs',mobile:'445'}

  //     ]
  //   })
  // }


  deletePersonHandler = (personIndex) => {
    // const persons = this.state.persons.slice();
    const persons = [...this.state.persons];
    persons.splice(personIndex,1);
    this.setState({persons:persons})
  }
  tagglePerson = () =>{
    const doesshow= this.state.showPersons
    {this.setState({showPersons:!doesshow})}
  }
  // nameChangeHandler = (event) => {
  //   this.setState({
  //     persons:[
  //       {name:'lavanya',mobile:'56'},
  //       {name:event.target.value,mobile:'445'}
  //     ]
  //   })
  // } 
  render(){ 

    let persons  = null;
    if (this.state.showPersons){
      persons = (
        <div>
          {this.state.persons.map((persons,index) =>{
            //alert(index);
            return <Person click ={()=> this.deletePersonHandler(index)}
            name={person.name}
            mobile={person.mobile}
            key = {person.id}/>

          })
          }
        {/* <
        Person name={this.state.persons[0].name} mobile={this.state.persons[0].mobile}/>
        <Person name={this.state.persons[1].name} mobile={this.state.persons[1].mobile}
        click ={this.switchNameHandler.bind(this,'sunitha')} changed = {this.nameChangeHandler}>Second Person</Person> */}
      </div> 
      );

    }

     console.log(this.state.navitabs[0].name);
    // if (this.navibar.showNavitabs){



    //     }

    const style = {
      backgroundColor:'white',
      font:'inherit',
      border:'1px solid blue',
      padding:'8px',
      cursor: 'pointer'
    };
    return (
      <div>
        {/* <Input />
        <Button /> */}
        <Header />
        <Navibar />
        {/* <Sidebar name={this.state.navitabs[0].navitabs} image={this.state.navitabs[0].image} /> */}
        <div  class="column side">
					{this.state.navitabs.map((navitabs, index) => {
						return (
							<Sidebar
								key={index}
								id={navitabs.id}
								name={navitabs.name}
								image={navitabs.image}
							/>
						);
          })}
          <button type="submit">Tweet</button> 

				</div>

        <div  class="column middle">
					{this.state.tweets.map((tweets  , index) => {
						return (
							<Content
								key={index}
                id={tweets.id}
                name={tweets.name}
                tweetid = {tweets.tweetid}
                content={tweets.content}

							/>
						);
          })}
				</div>
        {/* <Content /> */}
        {/* <Rightsidebar dataFromParent = {this.state.data} /> */}

        <Rightsidebar image={this.state.likecontent[0].image}
        level = {persons}
        name={this.state.likecontent[0].name} tweetid={this.state.likecontent[0].tweetid} />

        <button style={style} onClick ={()=> this.tagglePerson("test")}>Taggle Name</button>
         {/* {persons} 
         {navitabs}
               */}

      </div>
    );
  }

}
export default App;



