{
  return(
  <div class="navbar">
  <a href="#">Home</a>
  <a href="#">Link1</a>
  <a href="#">Link2</a>
  <a href="#" class="right">Log out</a>
</div>
  );
}
class App extends Component {
  state = {
    persons: [
      {id: 'sdfsd3', name: 'Lavanya', mobile:"123"},
      {id: 'sdfsdfsg3', name: 'Ganesh', mobile:"8093"},
      {id: '3', name: 'Venkatesh', mobile:"6676"},

    ],

    //showNavitabs : true
    showPersons: false

  }
  // navibar = {
