    //   );

    //     }

    const style = {
      backgroundColor:'white',
      font:'inherit',
      border:'1px solid blue',
      padding:'8px',
      cursor: 'pointer'
    };
    return (
      <div>
        {/* <Input />
        <Button /> */}
        <Header />
        <Navibar />
        <Sidebar name={this.state.persons[0].name} image={this.state.persons[0].mobile} />
        <Content />
        <Rightsidebar />
        <button style={style} onClick ={()=> this.tagglePerson("test")}>Taggle Name</button>
         {persons}      

      </div>
    );
  }

}
export default App;



